import React, { useEffect, useState } from 'react';
import API from '../../services/api';
import { useParams } from 'react-router-dom';
import styles from './GameDetails.module.css';

export default function GameDetails() {
  const { id } = useParams();
  const [game, setGame] = useState(null);
  useEffect(() => {
    API.get(`/games/${id}`).then(res => setGame(res.data));
  }, [id]);
  if (!game) return <p>Cargando…</p>;

  return (
    <div className={styles.container}>
      <h1>{game.nombre}</h1>
      <img src={game.imagenUrl} alt={game.nombre} className={styles.image}/>
      <p>{game.info}</p>
      <p>Jugabilidad: {game.jugabilidad} – Historia: {game.historia} – Gráficos: {game.graficos}</p>
      <a href={game.descargaUrl} className={styles.button} download>Descargar</a>
      {/* Más campos… */}
    </div>
  );
}
