import React, { useEffect, useState } from 'react';
import API from '../../services/api';
import styles from './Admin.module.css';

export default function Admin() {
  const [games, setGames] = useState([]);
  const [form, setForm]   = useState({
    extractUrl: '',
    nombre: '',
    imagenUrl: '',
    descargaUrl: '',
    jugabilidad: 0,
    historia: 0,
    graficos: 0,
    votos: 0,
    info: '',
    fechaEstreno: '',
    fechaActualizacion: '',
    actualizacion: '',
    plataforma: 'PC',
    idioma: '',
    audios: '',
    peso: '',
    version: '',
    requisitos: {
      minimo: { so: '', procesador: '', memoria: '', graficos: '', disco: '' },
      recomendado: { so: '', procesador: '', memoria: '', graficos: '', disco: '' },
    },
  });
  const [isEditing, setIsEditing] = useState(false);
  const [editId, setEditId] = useState(null);

  useEffect(() => loadGames(), []);
  const loadGames = () =>
    API.get('/games').then(res => setGames(res.data));

  const handleChange = e => {
    const { name, value } = e.target;
    if (name.startsWith('requisitos')) {
      const [_, level, field] = name.split('.');
      setForm(prev => ({
        ...prev,
        requisitos: {
          ...prev.requisitos,
          [level]: { ...prev.requisitos[level], [field]: value }
        }
      }));
    } else {
      setForm(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleExtract = async () => {
    try {
      const res = await API.post('/extract', { url: form.extractUrl });
      const { title, description } = res.data;
      setForm(prev => ({
        ...prev,
        nombre: title   || prev.nombre,
        info:   description || prev.info,
      }));
    } catch {
      alert('No se pudieron extraer los datos.');
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      if (isEditing) {
        await API.put(`/games/${editId}`, form);
      } else {
        await API.post('/games', form);
      }
      resetForm();
      loadGames();
    } catch {
      alert('Error al guardar.');
    }
  };

  const handleEdit = game => {
    setForm({
      extractUrl: '',
      nombre: game.nombre,
      imagenUrl: game.imagenUrl,
      descargaUrl: game.descargaUrl,
      jugabilidad: game.jugabilidad,
      historia: game.historia,
      graficos: game.graficos,
      votos: game.votos,
      info: game.info,
      fechaEstreno: game.fechaEstreno,
      fechaActualizacion: game.fechaActualizacion,
      actualizacion: game.actualizacion,
      plataforma: game.plataforma,
      idioma: game.idioma,
      audios: game.audios,
      peso: game.peso,
      version: game.version,
      requisitos: game.requisitos,
    });
    setIsEditing(true);
    setEditId(game._id);
  };

  const handleDelete = async id => {
    if (window.confirm('Â¿Eliminar este juego?')) {
      await API.delete(`/games/${id}`);
      loadGames();
    }
  };

  const resetForm = () => {
    setForm({
      extractUrl: '', nombre: '', imagenUrl: '', descargaUrl: '',
      jugabilidad: 0, historia: 0, graficos: 0, votos: 0,
      info: '', fechaEstreno: '', fechaActualizacion: '',
      actualizacion: '', plataforma: 'PC', idioma: '',
      audios: '', peso: '', version: '',
      requisitos: {
        minimo:    { so: '', procesador: '', memoria: '', graficos: '', disco: '' },
        recomendado: { so: '', procesador: '', memoria: '', graficos: '', disco: '' },
      },
    });
    setIsEditing(false);
    setEditId(null);
  };

  return (
    <div className={styles.container}>
      <h1>Admin - Juegos</h1>

      <form onSubmit={handleSubmit} className={styles.form}>
        {/* URL para extracciÃ³n */}
        <label>URL para extraer:</label>
        <input
          type="text" name="extractUrl"
          value={form.extractUrl}
          onChange={handleChange}
          className={styles.input}
        />
        <button type="button" onClick={handleExtract} className={styles.button}>
          Extraer datos
        </button>

        {/* Campos del juego */}
        <input name="nombre"        onChange={handleChange} placeholder="Nombre" value={form.nombre}/>
        <input name="imagenUrl"     onChange={handleChange} placeholder="URL imagen" value={form.imagenUrl}/>
        <input name="descargaUrl"   onChange={handleChange} placeholder="URL descarga" value={form.descargaUrl}/>
        <input name="jugabilidad"   type="number" onChange={handleChange} placeholder="Jugabilidad" value={form.jugabilidad}/>
        <input name="historia"      type="number" onChange={handleChange} placeholder="Historia" value={form.historia}/>
        <input name="graficos"      type="number" onChange={handleChange} placeholder="GrÃ¡ficos" value={form.graficos}/>
        <input name="votos"         type="number" onChange={handleChange} placeholder="Votos" value={form.votos}/>
        <textarea name="info" onChange={handleChange} placeholder="InformaciÃ³n" value={form.info}/>

        {/* Dates & otros */}
        <input name="fechaEstreno"      type="date" onChange={handleChange} value={form.fechaEstreno}/>
        <input name="fechaActualizacion" type="date" onChange={handleChange} value={form.fechaActualizacion}/>
        <input name="actualizacion"     onChange={handleChange} placeholder="DescripciÃ³n actualizaciÃ³n" value={form.actualizacion}/>
        <input name="plataforma"        onChange={handleChange} placeholder="Plataforma" value={form.plataforma}/>
        <input name="idioma"            onChange={handleChange} placeholder="Idioma" value={form.idioma}/>
        <input name="audios"            onChange={handleChange} placeholder="Audios" value={form.audios}/>
        <input name="peso"              onChange={handleChange} placeholder="Peso" value={form.peso}/>
        <input name="version"           onChange={handleChange} placeholder="VersiÃ³n" value={form.version}/>

        {/* Requisitos mÃ­nimos */}
        <h4>Requisitos MÃNIMOS</h4>
        <input name="requisitos.minimo.so"         onChange={handleChange} placeholder="SO mÃ­nimo"          value={form.requisitos.minimo.so}/>
        <input name="requisitos.minimo.procesador" onChange={handleChange} placeholder="Procesador mÃ­nimo"  value={form.requisitos.minimo.procesador}/>
        <input name="requisitos.minimo.memoria"    onChange={handleChange} placeholder="Memoria mÃ­nima"     value={form.requisitos.minimo.memoria}/>
        <input name="requisitos.minimo.graficos"   onChange={handleChange} placeholder="GrÃ¡ficos mÃ­nimos"   value={form.requisitos.minimo.graficos}/>
        <input name="requisitos.minimo.disco"      onChange={handleChange} placeholder="Disco mÃ­nimo"       value={form.requisitos.minimo.disco}/>

        {/* Requisitos recomendados */}
        <h4>Requisitos RECOMENDADOS</h4>
        <input name="requisitos.recomendado.so"         onChange={handleChange} placeholder="SO recomendado"         value={form.requisitos.recomendado.so}/>
        <input name="requisitos.recomendado.procesador" onChange={handleChange} placeholder="Procesador recomendado" value={form.requisitos.recomendado.procesador}/>
        <input name="requisitos.recomendado.memoria"    onChange={handleChange} placeholder="Memoria recomendada"    value={form.requisitos.recomendado.memoria}/>
        <input name="requisitos.recomendado.graficos"   onChange={handleChange} placeholder="GrÃ¡ficos recomendados"  value={form.requisitos.recomendado.graficos}/>
        <input name="requisitos.recomendado.disco"      onChange={handleChange} placeholder="Disco recomendado"      value={form.requisitos.recomendado.disco}/>

        {/* Botones de submit */}
        <button type="submit" className={styles.button}>
          {isEditing ? 'Actualizar Juego' : 'Guardar Juego'}
        </button>
        {isEditing && (
          <button type="button" onClick={resetForm} className={styles.button}>
            Cancelar ediciÃ³n
          </button>
        )}
      </form>

      <hr />

      <ul className={styles.list}>
        {games.map(game => (
          <li key={game._id} className={styles.item}>
            <span>{game.nombre}</span>
            <div>
              <button onClick={() => handleEdit(game)} className={styles.smallButton}>
                Editar
              </button>
              <button onClick={() => handleDelete(game._id)} className={styles.smallButton}>
                Eliminar
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
