import React, { useEffect, useState } from 'react';
import API from '../../services/api';
import styles from './Admin.module.css';

export default function Admin() {
  const [games, setGames] = useState([]);
  const [form, setForm]   = useState({
    nombre:'', imagenUrl:'', descargaUrl:'', jugabilidad:0, historia:0, graficos:0,
    votos:0, info:'', fechaEstreno:'', fechaActualizacion:'', actualizacion:'',
    plataforma:'PC', idioma:'', audios:'', peso:'', version:'',
    minimo:{ so:'', procesador:'', memoria:'', graficos:'', disco:''},
    recomendado:{ so:'', procesador:'', memoria:'', graficos:'', disco:''}
  });

  useEffect(() => { load(); }, []);
  const load = () => API.get('/games').then(res=>setGames(res.data));

  const handleChange = e => {
    const { name, value } = e.target;
    if (name.includes('minimo.') || name.includes('recomendado.')) {
      const [grp,key] = name.split('.');
      setForm(f=>({ ...f, [grp]:{ ...f[grp], [key]:value }}));
    } else setForm(f=>({ ...f, [name]:value }));
  };

  const handleSubmit = e => {
    e.preventDefault();
    API.post('/games', form).then(()=>{ load(); });
  };

  const handleDelete = id => API.delete(`/games/${id}`).then(()=>load());
  const handleEdit   = game => setForm({ ...game });

  return (
    <div className={styles.container}>
      <h2>Panel de Administraci√≥n</h2>
      <form className={styles.form} onSubmit={handleSubmit}>
        {/* Aqu√≠ repite inputs para cada campo, usando name= correspondiente */}
        <input name="nombre" onChange={handleChange} placeholder="Nombre" value={form.nombre}/>
        <input name="imagenUrl" onChange={handleChange} placeholder="URL imagen" value={form.imagenUrl}/>
        <input name="descargaUrl" onChange={handleChange} placeholder="URL descarga" value={form.descargaUrl}/>
        {/* ‚Ä¶m√°s inputs‚Ä¶ */}
        <button type="submit">Guardar</button>
      </form>
      <ul className={styles.list}>
        {games.map(g => (
          <li key={g._id}>
            {g.nombre}
            <button onClick={()=>handleEdit(g)}>‚úèÔ∏è</button>
            <button onClick={()=>handleDelete(g._id)}>üóëÔ∏è</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
