import React, { useEffect, useState } from 'react';
import API from '../../services/api';
import { Link } from 'react-router-dom';
import styles from './Home.module.css';

export default function Home() {
  const [games, setGames] = useState([]);
  const [filteredGames, setFilteredGames] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('name'); // name, rating, newest
  const [showSearchBar, setShowSearchBar] = useState(false);

  useEffect(() => {
    API.get('/games')
      .then(res => {
        setGames(res.data);
        setFilteredGames(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setLoading(false);
      });
  }, []);

  // Search and filter logic
  useEffect(() => {
    let filtered = [...games];

    // Filter by search term
    if (searchTerm.trim()) {
      filtered = filtered.filter(game =>
        game.nombre.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (game.info && game.info.toLowerCase().includes(searchTerm.toLowerCase())) ||
        (game.plataforma && game.plataforma.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }

    // Sort games
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'name':
          return a.nombre.localeCompare(b.nombre);
        case 'rating':
          const ratingA = (a.jugabilidad + a.historia + a.graficos) / 3 || 0;
          const ratingB = (b.jugabilidad + b.historia + b.graficos) / 3 || 0;
          return ratingB - ratingA;
        case 'newest':
          return new Date(b.fechaEstreno || 0) - new Date(a.fechaEstreno || 0);
        case 'votes':
          return (b.votos || 0) - (a.votos || 0);
        default:
          return 0;
      }
    });

    setFilteredGames(filtered);
  }, [games, searchTerm, sortBy]);

  const clearSearch = () => {
    setSearchTerm('');
    setShowSearchBar(false);
  };

  if (loading) {
    return (
      <div className={styles.loadingContainer}>
        <div className={styles.loader}></div>
        <p>Cargando juegos...</p>
      </div>
    );
  }

  return (
    <div className={styles.pageContainer}>
      {/* Hero Header */}
      <div className={styles.heroSection}>
        <div className={styles.heroContent}>
          <h1 className={styles.mainTitle}>Mega Juegos PC</h1>
          <p className={styles.subtitle}>
            Descubre los mejores juegos gratuitos para PC. Aventuras épicas te esperan.
          </p>
          <div className={styles.statsBar}>
            <div className={styles.statItem}>
              <span className={styles.statNumber}>{filteredGames.length}</span>
              <span className={styles.statLabel}>
                {searchTerm ? 'Resultados' : 'Juegos Disponibles'}
              </span>
            </div>
            <div className={styles.statItem}>
              <span className={styles.statNumber}>100%</span>
              <span className={styles.statLabel}>Gratis</span>
            </div>
            <div className={styles.statItem}>
              <span className={styles.statNumber}>∞</span>
              <span className={styles.statLabel}>Diversión</span>
            </div>
          </div>
        </div>
      </div>

      {/* Search and Filter Section */}
      <div className={styles.searchSection}>
        <div className={styles.searchContainer}>
          <div className={styles.searchBar}>
            <button 
              className={styles.searchToggle}
              onClick={() => setShowSearchBar(!showSearchBar)}
            >
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
                <path d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" 
                      stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
              Buscar juegos
            </button>
            
            {showSearchBar && (
              <div className={styles.searchInput}>
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none" className={styles.searchIcon}>
                  <path d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" 
                        stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                </svg>
                <input
                  type="text"
                  placeholder="Buscar por nombre, descripción o plataforma..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  autoFocus
                />
                {searchTerm && (
                  <button className={styles.clearSearch} onClick={clearSearch}>
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none">
                      <path d="M18 6L6 18M6 6l12 12" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
                    </svg>
                  </button>
                )}
              </div>
            )}
          </div>

          <div className={styles.filterControls}>
            <label className={styles.sortLabel}>
              Ordenar por:
              <select 
                value={sortBy} 
                onChange={(e) => setSortBy(e.target.value)}
                className={styles.sortSelect}
              >
                <option value="name">Nombre (A-Z)</option>
                <option value="rating">Mejor Valorados</option>
                <option value="newest">Más Recientes</option>
                <option value="votes">Más Votados</option>
              </select>
            </label>
          </div>
        </div>

        {searchTerm && (
          <div className={styles.searchResults}>
            <p>
              {filteredGames.length} resultado{filteredGames.length !== 1 ? 's' : ''} 
              para "<strong>{searchTerm}</strong>"
              {filteredGames.length > 0 && 
                <button onClick={clearSearch} className={styles.clearLink}>
                  Limpiar búsqueda
                </button>
              }
            </p>
          </div>
        )}
      </div>

      {/* Games Section */}
      <div className={styles.gamesSection}>
        <div className={styles.sectionHeader}>
          <h2 className={styles.sectionTitle}>
            {searchTerm ? `Resultados de búsqueda` : 'Todos los Juegos'}
          </h2>
          <div className={styles.gameCount}>
            {filteredGames.length} juego{filteredGames.length !== 1 ? 's' : ''} 
            {searchTerm ? ' encontrado' : ' disponible'}{filteredGames.length !== 1 ? 's' : ''}
          </div>
        </div>

        <div className={styles.gamesGrid}>
          {filteredGames.map(game => (
            <Link key={game._id} to={`/game/${game._id}`} className={styles.gameCard}>
              <div className={styles.imageContainer}>
                <img 
                  src={game.imagenUrl} 
                  alt={game.nombre}
                  className={styles.gameImage}
                />
                <div className={styles.overlay}>
                  <div className={styles.playButton}>
                    <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
                      <path d="M8 5v14l11-7z" fill="currentColor"/>
                    </svg>
                  </div>
                </div>
              </div>
              
              <div className={styles.gameInfo}>
                <h3 className={styles.gameTitle}>{game.nombre}</h3>
                <div className={styles.gameDetails}>
                  {game.plataforma && (
                    <span className={styles.platform}>{game.plataforma}</span>
                  )}
                  <span className={styles.price}>GRATIS</span>
                </div>
                
                {/* Ratings Preview */}
                {(game.jugabilidad || game.historia || game.graficos) && (
                  <div className={styles.ratingsPreview}>
                    {game.jugabilidad && (
                      <div className={styles.ratingItem}>
                        <span className={styles.ratingLabel}>Jugabilidad</span>
                        <span className={styles.ratingValue}>{game.jugabilidad}/10</span>
                      </div>
                    )}
                    {game.votos && (
                      <div className={styles.voteCount}>
                        {game.votos} votos
                      </div>
                    )}
                  </div>
                )}
              </div>
            </Link>
          ))}
        </div>

        {filteredGames.length === 0 && !loading && (
          <div className={styles.emptyState}>
            {searchTerm ? (
              <>
                <div className={styles.emptyIcon}>🔍</div>
                <h3>No se encontraron juegos</h3>
                <p>No hay juegos que coincidan con "<strong>{searchTerm}</strong>"</p>
                <button onClick={clearSearch} className={styles.clearButton}>
                  Ver todos los juegos
                </button>
              </>
            ) : (
              <>
                <div className={styles.emptyIcon}>🎮</div>
                <h3>No hay juegos disponibles</h3>
                <p>Parece que no hay juegos en la biblioteca actualmente.</p>
              </>
            )}
          </div>
        )}
      </div>
    </div>
  );
}
