// 3. Admin.js - Componente completo con extractor de URL

import React, { useEffect, useState } from 'react';
import API from '../../services/api';
import styles from './Admin.module.css';

export default function Admin() {
  const [games, setGames] = useState([]);
  const [urlToExtract, setUrlToExtract] = useState('');
  const [extracting, setExtracting] = useState(false);
  const [form, setForm] = useState({
    nombre: '',
    imagenUrl: '',
    descargaUrl: '',
    jugabilidad: 0,
    historia: 0,
    graficos: 0,
    votos: 0,
    info: '',
    fechaEstreno: '',
    fechaActualizacion: '',
    actualizacion: '',
    plataforma: 'PC',
    idioma: '',
    audios: '',
    peso: '',
    version: '',
    requisitos: {
      minimo: { so: '', procesador: '', memoria: '', graficos: '', disco: '' },
      recomendado: { so: '', procesador: '', memoria: '', graficos: '', disco: '' },
    },
  });

  useEffect(() => { loadGames(); }, []);
  
  const loadGames = () =>
    API.get('/games').then(res => setGames(res.data));

  const handleChange = e => {
    const { name, value } = e.target;
    if (name.startsWith('requisitos.')) {
      const [, group, key] = name.split('.');
      setForm(f => ({
        ...f,
        requisitos: {
          ...f.requisitos,
          [group]: {
            ...f.requisitos[group],
            [key]: value
          }
        }
      }));
    } else {
      setForm(f => ({ ...f, [name]: value }));
    }
  };

  // NUEVA FUNCI√ìN - Extraer datos de URL
  const handleExtractFromUrl = async () => {
    if (!urlToExtract.trim()) {
      alert('Por favor ingresa una URL v√°lida');
      return;
    }
    
    setExtracting(true);
    try {
      console.log('Extrayendo datos de:', urlToExtract);
      
      const response = await API.post('/games/extract-from-url', { 
        url: urlToExtract.trim() 
      });
      
      console.log('Respuesta del servidor:', response.data);
      
      if (response.data.success) {
        const extractedData = response.data.data;
        
        // Actualizar formulario con datos extra√≠dos
        setForm(prevForm => ({
          ...prevForm,
          // Sobrescribir con datos extra√≠dos
          nombre: extractedData.nombre || prevForm.nombre,
          imagenUrl: extractedData.imagenUrl || prevForm.imagenUrl,
          info: extractedData.info || prevForm.info,
          fechaEstreno: extractedData.fechaEstreno || prevForm.fechaEstreno,
          plataforma: extractedData.plataforma || prevForm.plataforma,
          peso: extractedData.peso || prevForm.peso,
          version: extractedData.version || prevForm.version,
          requisitos: {
            minimo: {
              so: extractedData.requisitos.minimo.so || prevForm.requisitos.minimo.so,
              procesador: extractedData.requisitos.minimo.procesador || prevForm.requisitos.minimo.procesador,
              memoria: extractedData.requisitos.minimo.memoria || prevForm.requisitos.minimo.memoria,
              graficos: extractedData.requisitos.minimo.graficos || prevForm.requisitos.minimo.graficos,
              disco: extractedData.requisitos.minimo.disco || prevForm.requisitos.minimo.disco,
            },
            recomendado: {
              so: extractedData.requisitos.recomendado.so || prevForm.requisitos.recomendado.so,
              procesador: extractedData.requisitos.recomendado.procesador || prevForm.requisitos.recomendado.procesador,
              memoria: extractedData.requisitos.recomendado.memoria || prevForm.requisitos.recomendado.memoria,
              graficos: extractedData.requisitos.recomendado.graficos || prevForm.requisitos.recomendado.graficos,
              disco: extractedData.requisitos.recomendado.disco || prevForm.requisitos.recomendado.disco,
            }
          }
        }));
        
        alert(`Datos extra√≠dos exitosamente!\n${response.data.message || ''}`);
        setUrlToExtract(''); // Limpiar URL
      } else {
        alert('Error al extraer datos: ' + response.data.error);
      }
    } catch (error) {
      console.error('Error completo:', error);
      if (error.response) {
        console.error('Respuesta del error:', error.response.data);
        alert(`Error del servidor: ${error.response.data.error || error.response.status}`);
      } else if (error.request) {
        alert('Error de red: No se pudo conectar con el servidor');
      } else {
        alert('Error: ' + error.message);
      }
    }
    setExtracting(false);
  };

  const handleSubmit = e => {
    e.preventDefault();
    API.post('/games', form).then(() => {
      setForm({
        nombre: '', imagenUrl: '', descargaUrl: '',
        jugabilidad: 0, historia: 0, graficos: 0, votos: 0,
        info: '', fechaEstreno: '', fechaActualizacion: '',
        actualizacion: '', plataforma: 'PC', idioma: '',
        audios: '', peso: '', version: '',
        requisitos: {
          minimo: { so: '', procesador: '', memoria: '', graficos: '', disco: '' },
          recomendado: { so: '', procesador: '', memoria: '', graficos: '', disco: '' },
        },
      });
      loadGames();
      alert('Juego guardado exitosamente!');
    }).catch(error => {
      console.error('Error al guardar:', error);
      alert('Error al guardar el juego');
    });
  };

  const handleDelete = id =>
    API.delete(`/games/${id}`).then(() => loadGames());

  const handleEdit = game =>
    setForm({
      ...game,
      fechaEstreno: game.fechaEstreno?.split('T')[0] || '',
      fechaActualizacion: game.fechaActualizacion?.split('T')[0] || '',
    });

  return (
    <div className={styles.container}>
      <h2>Panel de Administraci√≥n</h2>

      {/* NUEVO - Extractor de URL */}
      <div className={styles.urlExtractor}>
        <h3>üì• Extraer datos de URL</h3>
        <p className={styles.urlHelp}>
          Pega la URL de la p√°gina del juego para extraer autom√°ticamente la informaci√≥n
        </p>
        <div className={styles.urlInputGroup}>
          <input
            type="url"
            value={urlToExtract}
            onChange={(e) => setUrlToExtract(e.target.value)}
            placeholder="https://ejemplo.com/pagina-del-juego"
            className={styles.urlInput}
          />
          <button
            type="button"
            onClick={handleExtractFromUrl}
            disabled={extracting || !urlToExtract.trim()}
            className={styles.extractBtn}
          >
            {extracting ? '‚è≥ Extrayendo...' : 'üîç Extraer Datos'}
          </button>
        </div>
      </div>

      {/* Formulario existente */}
      <form className={styles.form} onSubmit={handleSubmit}>
        <h3>üìù Informaci√≥n del Juego</h3>
        
        {/* Datos b√°sicos */}
        <input name="nombre"        onChange={handleChange} placeholder="Nombre"         value={form.nombre}/>
        <input name="imagenUrl"     onChange={handleChange} placeholder="URL imagen"     value={form.imagenUrl}/>
        <input name="descargaUrl"   onChange={handleChange} placeholder="URL descarga"   value={form.descargaUrl}/>
        <input name="jugabilidad"   type="number" onChange={handleChange} placeholder="Jugabilidad (1-10)"     value={form.jugabilidad}/>
        <input name="historia"      type="number" onChange={handleChange} placeholder="Historia (1-10)"        value={form.historia}/>
        <input name="graficos"      type="number" onChange={handleChange} placeholder="Gr√°ficos (1-10)"        value={form.graficos}/>
        <input name="votos"         type="number" onChange={handleChange} placeholder="N¬∞ Votos"        value={form.votos}/>
        <textarea name="info"       onChange={handleChange} placeholder="Informaci√≥n del juego" value={form.info} rows="4"/>

        {/* Fechas & versi√≥n */}
        <input name="fechaEstreno"      type="date" onChange={handleChange} placeholder="Fecha de estreno"      value={form.fechaEstreno}/>
        <input name="fechaActualizacion" type="date" onChange={handleChange} placeholder="Fecha de actualizaci√≥n" value={form.fechaActualizacion}/>
        <input name="actualizacion"     onChange={handleChange} placeholder="Descripci√≥n actualizaci√≥n" value={form.actualizacion}/>
        <select name="plataforma"       onChange={handleChange} value={form.plataforma}>
          <option value="PC">PC</option>
          <option value="Mac">Mac</option>
          <option value="Linux">Linux</option>
          <option value="Multiplataforma">Multiplataforma</option>
        </select>
        <input name="idioma"            onChange={handleChange} placeholder="Idioma"                 value={form.idioma}/>
        <input name="audios"            onChange={handleChange} placeholder="Audios"                 value={form.audios}/>
        <input name="peso"              onChange={handleChange} placeholder="Peso (ej: 50 GB)"       value={form.peso}/>
        <input name="version"           onChange={handleChange} placeholder="Versi√≥n"                value={form.version}/>

        {/* Requisitos M√çNIMOS */}
        <h4>üíª Requisitos M√çNIMOS</h4>
        <input name="requisitos.minimo.so"         onChange={handleChange} placeholder="SO m√≠nimo"          value={form.requisitos.minimo.so}/>
        <input name="requisitos.minimo.procesador" onChange={handleChange} placeholder="Procesador m√≠nimo"  value={form.requisitos.minimo.procesador}/>
        <input name="requisitos.minimo.memoria"    onChange={handleChange} placeholder="Memoria m√≠nima"     value={form.requisitos.minimo.memoria}/>
        <input name="requisitos.minimo.graficos"   onChange={handleChange} placeholder="Gr√°ficos m√≠nimos"   value={form.requisitos.minimo.graficos}/>
        <input name="requisitos.minimo.disco"      onChange={handleChange} placeholder="Disco m√≠nimo"       value={form.requisitos.minimo.disco}/>

        {/* Requisitos RECOMENDADOS */}
        <h4>üöÄ Requisitos RECOMENDADOS</h4>
        <input name="requisitos.recomendado.so"         onChange={handleChange} placeholder="SO recomendado"         value={form.requisitos.recomendado.so}/>
        <input name="requisitos.recomendado.procesador" onChange={handleChange} placeholder="Procesador recomendado" value={form.requisitos.recomendado.procesador}/>
        <input name="requisitos.recomendado.memoria"    onChange={handleChange} placeholder="Memoria recomendada"    value={form.requisitos.recomendado.memoria}/>
        <input name="requisitos.recomendado.graficos"   onChange={handleChange} placeholder="Gr√°ficos recomendados"  value={form.requisitos.recomendado.graficos}/>
        <input name="requisitos.recomendado.disco"      onChange={handleChange} placeholder="Disco recomendado"      value={form.requisitos.recomendado.disco}/>

        <button type="submit" className={styles.saveBtn}>üíæ Guardar Juego</button>
      </form>

      {/* Lista de juegos */}
      <div className={styles.gamesList}>
        <h3>üéÆ Juegos Registrados</h3>
        <ul className={styles.list}>
          {games.map(g => (
            <li key={g._id} className={styles.gameItem}>
              <span className={styles.gameName}>{g.nombre}</span>
              <div className={styles.gameActions}>
                <button onClick={() => handleEdit(g)} className={styles.editBtn}>‚úèÔ∏è Editar</button>
                <button onClick={() => handleDelete(g._id)} className={styles.deleteBtn}>üóëÔ∏è Eliminar</button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
