import React, { useEffect, useState } from 'react';
import API from '../../services/api';
import styles from './Admin.module.css';

export default function Admin() {
  const [games, setGames] = useState([]);
  const [form, setForm]   = useState({
    extractUrl: '',
    nombre: '',
    imagenUrl: '',
    descargaUrl: '',
    jugabilidad: 0,
    historia: 0,
    graficos: 0,
    votos: 0,
    info: '',
    fechaEstreno: '',
    fechaActualizacion: '',
    actualizacion: '',
    plataforma: 'PC',
    idioma: '',
    audios: '',
    peso: '',
    version: '',
    requisitos: {
      minimo: { so: '', procesador: '', memoria: '', graficos: '', disco: '' },
      recomendado: { so: '', procesador: '', memoria: '', graficos: '', disco: '' },
    },
  });
  const [isEditing, setIsEditing] = useState(false);
  const [editId, setEditId] = useState(null);

  useEffect(() => { loadGames(); }, []);
  const loadGames = () =>
    API.get('/games').then(res => setGames(res.data));

  const handleChange = e => {
    const { name, value } = e.target;
    if (name.startsWith('requisitos')) {
      const [_, level, field] = name.split('.');
      setForm(prev => ({
        ...prev,
        requisitos: {
          ...prev.requisitos,
          [level]: { ...prev.requisitos[level], [field]: value }
        }
      }));
    } else {
      setForm(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleExtract = async () => {
    try {
      const res = await API.post('/extract', { url: form.extractUrl });
      const data = res.data;
      setForm(prev => ({
        ...prev,
        nombre: data.title || prev.nombre,
        info: data.description || prev.info,
        // imagenUrl: data.imagenUrl || prev.imagenUrl,
        // descargaUrl: data.descargaUrl || prev.descargaUrl,
      }));
    } catch (err) {
      console.error('Error al extraer:', err);
      alert('No se pudieron extraer los datos.');
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      if (isEditing) {
        await API.put(`/games/${editId}`, form);
      } else {
        await API.post('/games', form);
      }
      resetForm();
      loadGames();
    } catch (err) {
      console.error('Error guardando:', err);
      alert('Ocurrió un error al guardar.');
    }
  };

  const handleEdit = game => {
    // Carga el juego en el formulario para editar
    setForm({
      extractUrl: '',
      nombre: game.nombre,
      imagenUrl: game.imagenUrl,
      descargaUrl: game.descargaUrl,
      jugabilidad: game.jugabilidad,
      historia: game.historia,
      graficos: game.graficos,
      votos: game.votos,
      info: game.info,
      fechaEstreno: game.fechaEstreno,
      fechaActualizacion: game.fechaActualizacion,
      actualizacion: game.actualizacion,
      plataforma: game.plataforma,
      idioma: game.idioma,
      audios: game.audios,
      peso: game.peso,
      version: game.version,
      requisitos: game.requisitos,
    });
    setIsEditing(true);
    setEditId(game._id);
  };

  const handleDelete = async id => {
    if (window.confirm('¿Seguro que quieres eliminar este juego?')) {
      try {
        await API.delete(`/games/${id}`);
        loadGames();
      } catch (err) {
        console.error('Error al eliminar:', err);
        alert('No se pudo eliminar.');
      }
    }
  };

  const resetForm = () => {
    setForm({
      extractUrl: '',
      nombre: '',
      imagenUrl: '',
      descargaUrl: '',
      jugabilidad: 0,
      historia: 0,
      graficos: 0,
      votos: 0,
      info: '',
      fechaEstreno: '',
      fechaActualizacion: '',
      actualizacion: '',
      plataforma: 'PC',
      idioma: '',
      audios: '',
      peso: '',
      version: '',
      requisitos: {
        minimo: { so: '', procesador: '', memoria: '', graficos: '', disco: '' },
        recomendado: { so: '', procesador: '', memoria: '', graficos: '', disco: '' },
      },
    });
    setIsEditing(false);
    setEditId(null);
  };

  return (
    <div className={styles.container}>
      <h1>Admin - Juegos</h1>

      <form onSubmit={handleSubmit} className={styles.form}>
        <label>URL para extraer:</label>
        <input
          type="text"
          name="extractUrl"
          value={form.extractUrl}
          onChange={handleChange}
          className={styles.input}
        />
        <button type="button" onClick={handleExtract} className={styles.button}>
          Extraer datos
        </button>

        {/* Aquí van el resto de inputs (nombre, imagenUrl, etc.) idénticos a como los tenías */}
        
        <button type="submit" className={styles.button}>
          {isEditing ? 'Actualizar Juego' : 'Guardar Juego'}
        </button>
        {isEditing && (
          <button type="button" onClick={resetForm} className={styles.button}>
            Cancelar edición
          </button>
        )}
      </form>

      <hr />

      <ul className={styles.list}>
        {games.map(game => (
          <li key={game._id} className={styles.item}>
            <span>{game.nombre}</span>
            <div>
              <button onClick={() => handleEdit(game)} className={styles.smallButton}>
                Editar
              </button>
              <button onClick={() => handleDelete(game._id)} className={styles.smallButton}>
                Eliminar
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
